"use server";

import { PaySdk } from "@rccpr/sdk";

const sdk = new PaySdk({
});

export const payWithProvider = async (
  provider: string,
  amount: number,
  currency = "USD"
) => {
  const { link } = await sdk.requestPaymentLink({
    service: 'stripe',
    selectedCurrency:  "USD",
    unitBase: 1000,
    referenceId: "123",
  });

  return  'https://www.paymentcli.xyz' + link;;
};

export const getSupportedPaymentMethods = async () => {
  return await sdk.getSupportedProviderForCurrency("USD");
};
